import React, {Component} from 'react';

class EditEvents extends Component {
    constructor(props){
        super(props);
        this.state = {
            idNumber:'',
            location:'',
            data:'',


        };
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleIdNumberChange = this.handleIdNumberChange.bind(this);
        this.handleLocationChange = this.handleLocationChange.bind(this);
        this.handledatachange = this.handledatachange.bind(this);
    }
    handleIdNumberChange(event){
        this.setState({
            idNumber : event.target.value
        });
    }
    handleLocationChange(event){
        this.setState({
            location : event.target.value
        });
    }

    handledatachange(event){
        this.setState({
            data : event.target.value
        });
    }
    handleSubmit(event){
        // alert('getting Id'+ this.state.value);
        event.preventDefault();
        this.props.editEvent(
            this.state.idNumber,
            this.state.location,
            this.state.data
        );
    //     fetch(`/events/${this.state.idNumber}`)
    //     .then(res => {
    //         console.log(res)
    //         return res.json()})
    //       .then(
    //         (result) => {
    //           console.log(result)
    //           this.setState({
    //             location:'',
    //             data:''
    //           });
    //     })
    //     .catch((error) => {
    //         throw (error);
    //     });
    // event.preventDefault();
}
    render(){
        return(
            <form onSubmit={this.handleSubmit}>
            EditEvent by ID<br />
                <label>
                    <input value={this.state.idNumber} onChange={this.handleIdNumberChange} />
                    <input placeholder="location" type="text" value={this.state.location} onChange={this.handleLocationChange}/>
                    <input placeholder="date" type="text" value={this.state.data} onChange={this.handledatachange}/>
                </label>
                <input type='submit' value='submit' />
            </form>
        );
    }
}



export default EditEvents;
