import React, {Component} from 'react';
import './PostNewEvent.css';
class PostNewEvent extends Component {
    constructor(props){
     super(props);
     this.state={ location:"", data:""} 
     this.handleSubmit = this.handleSubmit.bind(this);

    }

    handleSubmit(event){ 
     event.preventDefault();
     fetch('/event', {
      method: 'post',
      headers: {'Content-Type':'application/json'},
      body: {
       location: this.Location.value,
       data:this.data.value
      }
     });
    };

    render () {
     return (
      <div id="signup">
       <form onSubmit={this.handleSubmit}>
        <input placeholder="location" type="text" value={this.state.location} onChange={(ev)=>this.setState({location:ev.target.value})}/>
        <input placeholder="date" type="text" value={this.state.data} onChange={(ev)=>this.setState({data:ev.target.value})}/>
          <button type="Submit">Add an Event</button>
       </form>
      </div>
     )
    }
   }



export default PostNewEvent;
