import React, {Component} from 'react';
import './PostNewEvent.css';
class PostNewEvent extends Component {
    constructor(props){
     super(props);
     this.handleSubmit = this.handleSubmit.bind(this);
     this.state={ firstname:"", lastname:""} 
    }

    handleSubmit(event){ 
     event.preventDefault();
     fetch('/', {
      method: 'post',
      headers: {'Content-Type':'application/json'},
      body: {
       "location": this.Location.value,
       'data':this.data.value
      }
     });
    };

    render () {
     return (
      <div id="signup">
       <form onSubmit={this.handleSubmit}>
           <input ref={(ref) => {this.Location = ref}} placeholder="location" type="text" name="location"/><br />
           <input ref={(ref) => {this.data = ref}} placeholder="date" type="text" name="date"/><br />
          <button type="Submit">Add an Event</button>
       </form>
      </div>
     )
    }
   }



export default PostNewEvent;
