import React, {Component} from 'react';
import './gettingAllEvents.css';
import PostNewEvent from './PostNewEvent';
import DeleteEvent from './deleteEvent';

class GettingAllEvents extends Component{
  constructor() {
      super();
      this.state = {
        error: null,
        isLoaded: false,
        items: []
      };
    }
    handleDelete =(id)=>{
      console.log(id);
      this.setState({
        items:this.state.items.filter(( item =>{
          return parseInt(item.id) !== id;
        }))


      })
    }
    handleAddEvent = (location, data)=>{
      const body ={
        location:location,
        data: data
      }
      fetch('/events', {
          method: 'post',
          body: JSON.stringify(body),
          headers: {'Content-Type':'application/json'},
         }).then(json =>{console.log(json);});

    this.setState({
      items:[
        ...this.state.items,
        {
          location:location,
          data: data
        }
      ]
    })
  }

    componentDidMount() {
      fetch("/events")
        .then(res => {
          console.log(res)
          return res.json()})
        .then(
          (result) => {
            console.log(result)
            this.setState({
              isLoaded: true,
              items: result
            });
          },
          // Note: it's important to handle errors here
          // instead of a catch() block so that we don't swallow
          // exceptions from actual bugs in components.
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
    }

    render() {
      const { error, isLoaded, items } = this.state;
      if (error) {
        return <div>Error: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {
        return (
          <div>
          <ul>
            {items.map(item => (
              <li key={item.id}>
                {item.id} {item.location}
              </li>
            ))}
          </ul>
          <PostNewEvent addEvent={this.handleAddEvent} />
          <DeleteEvent deleteEvent={this.handleDelete} />
          </div>

        );
      }
    }

  }


export default GettingAllEvents;
